// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.26;

// --- Diamond data structures (inline to avoid imports) ---
enum FacetCutAction {
    ADD,
    REPLACE,
    REMOVE
}

struct FacetCut {
    address target;
    FacetCutAction action;
    bytes4[] selectors;
}

struct Facet {
    address target;
    bytes4[] selectors;
}

// --- Condition Precedent data ---
struct Condition {
    uint256 id;
    string description;
    bytes32 docHash;
    bool isSatisfied;
    uint256 satisfiedAt;
    string docReference;
}

// --- Credit Facility data ---
enum FacilityStatus {
    Pending,
    Active,
    Suspended,
    Terminated
}

enum DrawdownStatus {
    Requested,
    Approved,
    Rejected,
    Disbursed,
    Repaid,
    Defaulted
}

enum RateType {
    Fixed,
    SOFR
}

struct FacilityTerms {
    uint256 totalCommitment;
    uint256 availableAmount;
    RateType rateType;
    uint256 fixedRateBps;
    uint256 spreadBps;
    uint256 arrangementFeeBps;
    uint256 commitmentFeeBps;
    uint256 monitoringFeeAnnualAmount;
    uint256 availabilityPeriodEnd;
    uint256 gracePeriodSeconds;
}

struct Drawdown {
    uint256 id;
    uint256 principalRequested;
    uint256 principalOutstanding;
    uint256 requestDate;
    uint256 disbursementDate;
    uint256 maturityDate;
    string fiatReference;
    DrawdownStatus status;
    uint256 accruedInterest;
    uint256 paidInterest;
    uint256 lastAccrualDate;
}

struct InterestSummary {
    uint256 drawdownId;
    uint256 principalOutstanding;
    uint256 accruedInterest;
    uint256 paidInterest;
    uint256 unpaidInterest;
    uint256 currentRateBps;
    uint256 lastAccrualDate;
    uint256 daysSinceLastAccrual;
}

/**
 * @title ICreditFacilitySystemExplicit
 * @notice Flattened interface that lists every external function exposed by the CreditFacilitySystem diamond.
 * @dev Useful for explicit ABI generation or audits without following inherited interfaces.
 */
interface ICreditFacilitySystemExplicit {
    // --- PackageController (diamond upgrade) ---
    function diamondCut(FacetCut[] calldata facetCuts, address target, bytes calldata data) external;

    // --- PackageViewer (diamond introspection) ---
    function facets() external view returns (Facet[] memory diamondFacets);
    function facetFunctionSelectors(address facet) external view returns (bytes4[] memory selectors);
    function facetAddresses() external view returns (address[] memory addresses);
    function facetAddress(bytes4 selector) external view returns (address facet);

    // --- AccessControl (shared across packages) ---
    function hasRole(bytes32 role, address account) external view returns (bool);
    function getRoleAdmin(bytes32 role) external view returns (bytes32);
    function grantRole(bytes32 role, address account) external;
    function revokeRole(bytes32 role, address account) external;
    function renounceRole(bytes32 role) external;
    function getRoleMember(bytes32 role, uint256 index) external view returns (address);
    function getRoleMemberCount(bytes32 role) external view returns (uint256);

    // --- ConditionPrecedent ---
    function ConditionPrecedent_init(address defaultAdmin_, address cpAdmin_) external;
    function addCondition(string calldata _description) external;
    function satisfyCondition(uint256 _cpId, bytes32 _docHash, string calldata _docRef) external;
    function revokeCondition(uint256 _cpId, string calldata _reason) external;
    function isFacilityLive() external view returns (bool);
    function getCondition(uint256 _cpId) external view returns (Condition memory);
    function getPendingConditions() external view returns (uint256[] memory);

    // --- RateOracle ---
    function RateOracle_init(address defaultAdmin_, address oracleAdmin_) external;
    function updateRate(uint256 _rate, uint256 _timestamp) external;
    function getRate(uint256 _timestamp) external view returns (uint256);
    function getLatestRate() external view returns (uint256 rate, uint256 timestamp);

    // --- InterestCalculator ---
    function calculateInterestForDrawdown(uint256 drawdownId) external view returns (uint256 interest);
    function calculatePendingInterest(uint256 drawdownId) external view returns (uint256 interest);

    // --- CreditFacility: activation & lifecycle ---
    function activateFacility(
        address lender_,
        address borrower_,
        uint256 totalCommitment_,
        RateType rateType_,
        uint256 fixedRateBps_,
        uint256 spreadBps_,
        uint256 arrangementFeeBps_,
        uint256 commitmentFeeBps_,
        uint256 monitoringFeeAnnualAmount_,
        uint256 availabilityDuration_,
        uint256 gracePeriod_
    ) external;

    function suspendFacility(string calldata _reason) external;
    function reinstateFacility() external;
    function terminateFacility(string calldata _reason) external;

    // --- CreditFacility: fees ---
    function invoiceArrangement() external;
    function payArrangement() external;

    // --- CreditFacility: drawdowns ---
    function requestDrawdown(uint256 _amount) external;
    function approveDrawdown(uint256 _drawdownId) external;
    function rejectDrawdown(uint256 _drawdownId, string calldata _reason) external;
    function recordDisbursement(uint256 _drawdownId, string calldata _fiatRef) external;
    function recordRepayment(uint256 _drawdownId, uint256 _amount) external;
    function declareDefault(uint256 _drawdownId, string calldata _reason) external;

    // --- CreditFacility: interest accrual ---
    function accrueInterest(uint256 _drawdownId) external;
    function accrueInterestBatch(uint256[] calldata _drawdownIds) external;
    function recordInterestPayment(uint256 _drawdownId, uint256 _amount) external;

    // --- CreditFacility: views ---
    function getDrawdown(uint256 _drawdownId) external view returns (Drawdown memory);
    function getFacilityTerms() external view returns (FacilityTerms memory);
    function getArrangementFeeAmount() external view returns (uint256);
    function calculateCommitmentFee(uint256 _duration) external view returns (uint256);
    function calculateMonitoringFee(uint256 _duration) external view returns (uint256);
    function calculateCurrentInterestRate() external view returns (uint256);
    function getInterestSummary(uint256 _drawdownId) external view returns (InterestSummary memory);
    function calculateAccruedInterest(uint256 _drawdownId) external view returns (uint256);
    function getTotalUnpaidInterest() external view returns (uint256);
}

